command:example.dev:code:
$ExceptionFiledID = (Get-TDCustomAttribute -ComponentID Asset | Where-Object Name -eq 'ExceptionFiled').ID
$ExceptionFiledYesID = ((Get-TDCustomAttribute -ComponentID Asset | Where-Object Name -eq 'ExceptionFiled').Choices | Where-Object Name -eq 'Yes').ID
New-TDAsset -SerialNumber 1055 - StatusID 3042 -Attributes @{ID=$ExceptionFiledID;Value=$ExceptionFiledYesID}

$ExceptionFiledID = (Get-TDCustomAttribute -ComponentID Asset | Where-Object Name -eq 'Exception Filed').ID
$ExceptionFiledYesID = ((Get-TDCustomAttribute -ComponentID Asset | Where-Object Name -eq 'Exception Filed').Choices | Where-Object Name -eq 'Yes').ID
Set-TDAsset -ID 5504 -Attributes @{ID=$ExceptionFiledID;Value=$ExceptionFiledYesID}

$OutOfSupportID = (Get-TDCustomAttribute -ComponentID ProductModel | Where-Object Name -eq 'OutOfSupport').ID
$OutOfSupportYesID = ((Get-TDCustomAttribute -ComponentID ProductModel | Where-Object Name -eq 'OutOfSupport').Choices | Where-Object Name -eq 'Yes').ID
Set-TDProductModel -ID 71292 -Attributes @{ID=$OutOfSupportID;Value=$OutOfSupportYesID}
